package com.example.stores.Service;import com.example.stores.DTO.UserDTO;import jakarta.servlet.http.HttpSession;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.mail.SimpleMailMessage;import org.springframework.stereotype.Service;import org.springframework.mail.javamail.JavaMailSender;import java.time.LocalDate;@Service@RequiredArgsConstructor@Slf4jpublic class EmailSendingServiceImplentation {    private final JavaMailSender javaMailSender;    public void sendEmail(UserDTO userDTO, Long otp){        SimpleMailMessage sendTo = new SimpleMailMessage();        sendTo.setFrom("temple.chiorlu@decagon.dev");        sendTo.setTo(userDTO.getEmail());        sendTo.setText("Hello\n" +                "you are have succesfully sign up\n" +                "Enter the OTP BELOW "+ otp+                "\n" +userDTO.getEmail()                );        sendTo.setSubject("SIGN UP PORTAL");        javaMailSender.send(sendTo);        log.info("Successfully signup");    }}